<script>
    $(document).on('click','.del_order',function(){
        var order_id = $(this).data('id');
        $.ajax({
            url:"whdorder_list",
            type:"post",
            data:{
                order_id:order_id
            },
            dataType:"json",
            success:function(msg){
                if(msg == 1){
                    location.href = 'whdorder_list';
                }
            }
        })
    })

    $(document).on('click','.update_order',function(){
        var order_id = $(this).data('id');
        $.ajax({
            url:"owhd_saveorder",
            type:"post",
            data:{
                order_id:order_id
            },
            dataType:"json",
            success:function(msg){
                if(msg == 1){
                    location.href = 'whdorder_list';
                }
            }
        })
    })
</script>